CREATE TABLE `MESSAGE`(
	ID				INT 	PRIMARY KEY AUTO_INCREMENT,
	MESSAGEID 		VARCHAR(255) 	NOT NULL,
	SENDID			VARCHAR(255) 	NOT NULL,
	RECID			VARCHAR(255)		NOT NULL,
	STATES			SMALLINT		NOT NULL
);

CREATE TABLE `MESSAGECONTENT`(
	MESSAGEID		VARCHAR(255) 	PRIMARY KEY ,
	CONTENT 		BLOB	NOT NULL,
	PDATE			VARCHAR(255)	NOT NULL
);

CREATE TABLE `MEMBERIMAGE`(
	ID				INT 			PRIMARY KEY AUTO_INCREMENT,
	MEMUUID 		VARCHAR(64) 	NOT NULL,
	IMAGEURL		VARCHAR(200) 	NOT NULL
);

CREATE TABLE `MEMBER` (
  `MEMUUID` VARCHAR(255) NOT NULL,
  `MEMSEX` INT DEFAULT NULL,
  `MEMMAIL` VARCHAR(255) DEFAULT NULL,
  `MEMPWD` VARCHAR(255) DEFAULT NULL,
  `MEMDEP` VARCHAR(255) DEFAULT NULL,
  `MEMNICKNAME` VARCHAR(255) DEFAULT NULL,
  `MEMADDRESS` VARCHAR(255) DEFAULT NULL,
  `MEMAGE` INT DEFAULT NULL,
  `MEMALCOHOL` INT DEFAULT NULL,
  `MEMASSETS` INT DEFAULT NULL,
  `MEMBIRTHDAY` VARCHAR(255) DEFAULT NULL,
  `MEMEDU` INT DEFAULT NULL,
  `MEMHEIGHT` INT DEFAULT NULL,
  `MEMINCOME` INT DEFAULT NULL,
  `MEMISVIP` INT DEFAULT NULL,
  `MEMLGD` INT DEFAULT NULL,
  `MEMMARRY` INT DEFAULT NULL,
  `MEMNAME` VARCHAR(255) DEFAULT NULL,
  `MEMPHONE` VARCHAR(255) DEFAULT NULL,
  `MEMRDATE` VARCHAR(255) DEFAULT NULL,
  `MEMSMOKE` INT DEFAULT NULL,
  `MEMSTA` INT DEFAULT NULL,
  `MEMWEIGHT` INT DEFAULT NULL,
  `MEMPICURL` VARCHAR(500) DEFAULT NULL,
  PRIMARY KEY (`MEMUUID`)
) ;







